import React, { useEffect, useState } from "react";
import { getAllLaunchList } from "./Utility/Services/launch";

const LaunchButton = () => {
  let buttonList = [];
  for (let i = 2006; i <= 2020; i++) {
    buttonList.push(
      <div className="col-lg-6">
        <button className="btn btn-secondary mt-4">{i}</button>
      </div>
    );
  }
  return buttonList;
};
const SpaceLaunchList = () => {
  const [state, setState] = useState({
    launchList: [],
    launch_success: false,
    land_success: false,
    year: "",
    loading: false,
  });
  const handleChange = (name, value) => {
    state[name] = value;
    GetAllList(state.year, state.land_success, state.launch_success);
    setState({ ...state });
  };
  useEffect(() => {
    GetAllList("", false, false);
  }, []);
  const GetAllList = (year, landing, launch) => {
    state.loading = true;
    setState({ ...state });
    getAllLaunchList(year, landing, launch).then((res) => {
      if (res.error) {
        state.loading = false;
        setState({ ...state });
        return;
      }
      if (!res.error) {
        state.loading = false;
        state.launchList = res.data;
        setState({ ...state });
      }
      setState({ loading: true });
    });
  };
  return (
    <>
      <div className="container-fluid">
        <div className="row">
          <div className="col-lg-4">
            <span>Launch Year</span>
            <div className="row">
              <LaunchButton />
            </div>
            <hr />
            <span>Success launch</span>

            <div className="row">
              <div className="col-lg-6">
                {" "}
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    handleChange("launch_success", true);
                  }}
                >
                  True
                </button>{" "}
              </div>
              <div className="col-lg-6">
                <button
                  className="btn btn-primary"
                  onClick={() => {
                    handleChange("launch_success", false);
                  }}
                >
                  False
                </button>
              </div>
            </div>
          </div>
          <div className="col-lg-8">
            <div className="row">
              {state.launchList.map((res) => {
                return (
                  <div className="col-lg-3 border">
                    <div>
                      {" "}
                      <img src={res.links.mission_patch_small} />{" "}
                    </div>
                    <div> {res.mission_name} </div>
                    <div>
                      {" "}
                      Mission Ids
                      <ul>
                        {" "}
                        {res.mission_id.map((idData) => {
                          return <li>{idData}</li>;
                        })}{" "}
                      </ul>
                    </div>
                    <div>Launch year :{res.launch_year}</div>
                    <div>
                      Successful Launch :{res.launch_success ? "True" : "False"}
                    </div>
                    <div>Successful Landing :{res.launch_year}</div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default SpaceLaunchList;
